import { useEffect, useRef, useState } from "react";
import "./App.css";

import { io } from "socket.io-client";

function App() {
  const socket = useRef();
  // State for storing messages and current message
  const [messages, setMessages] = useState([]);
  const [currentMessage, setCurrentMessage] = useState({
    user: "",
    message: "",
  });
  // Function to send a message
  const sendMessage = () => {
    if (currentMessage) {
      socket.current.emit("message", currentMessage);
      setCurrentMessage({
        user: "",
        message: "",
      });
    }
  };

  useEffect(() => {
    socket.current = io("ws://localhost:9013");

    socket.current.on("connnection", () => {
      console.log("connected to server");
    });
  }, []);

  useEffect(() => {
    // Listen for incoming messages
    socket.current.on("message", (message) => {
      console.log(message);
      // setMessages((prevMessages) => [...prevMessages, message]);
    });
  }, []);

  return (
    <div className="App">
      <div className="chat-wrapper">
        <div className="messages">
          {messages.map((message, index) => (
            <div key={index} className="message">
              {message.message} from {message.user}
            </div>
          ))}
        </div>
        <input
          type="text"
          placeholder="input your name"
          value={currentMessage.user}
          onChange={(e) =>
            setCurrentMessage((prev) => ({ ...prev, user: e.target.value }))
          }
          required
        />
        <input
          type="text"
          placeholder="Type a message..."
          value={currentMessage.message}
          onChange={(e) =>
            setCurrentMessage((prev) => ({ ...prev, message: e.target.value }))
          }
          required
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default App;
